/* ==UserStyle==
@name           soundcloud.com
@namespace      github.com/openstyles/stylus
@version        0.4.1
@description    A Nord theme for Soundcloud
@author         Seb day
==/UserStyle== */
@-moz-document domain("soundcloud.com") {

    * {
        font-family: monospace!important;
    }
    .theme-dark {
        --surface-color: #292e42;
        --background-surface-color: #0a0e19;
        --highlight-color: #0a0e19;
        --button-secondary-background-color: #0a0e19;
        --button-secondary-selected-background-color: #0a0e19;
    }

    .header__upsellWrapper,
    .header__forArtistsButton,
    .header__soundInput,
    .l-sidebar-right,
    .queue__fallback,
    .backgroundGradient,
    .imageChooser,
    a[data-menu-name="home"] {
        display: none!important;
    }

    .l-container {
        width: 935px;
    }
    .l-search .l-fixed-left {
        width: 300px;
    }
    .l-fluid-fixed .l-main {
        margin: 0;
    }

    .l-search .l-main {
        margin: 0 0 0 300px;
    }

    .searchOptions__scrollable {
        width: 300px;
    }

    .l-listen-wrapper .l-about-main {
        margin: 0;
    }

    .l-about-main {
        padding-top: 0!important;
    }

    .playControls__queue {
        bottom: 48px;
    }

    .queue__itemsHeight {
        background-size: auto!important;
    }

    h1 {
        color: #0a0e19!important;
    }

    .queue__itemWrapper,
    .theme-dark .queue__itemsHeight,
    .queue,
    .sc-button-like,
    .g-type-shrinkwrap-inline {
        background-color: #0a0e19!important;
    }

    .g-type-shrinkwrap-block {
        background: transparent;
        color: #b2f8f8!important;
    }
    .g-type-shrinkwrap-inline {
        color: #b2f8f8!important;
        margin-top: 5px;
    }
    .sc-button-xxlarge {
        background-color: #b2f8f8!important;
    }
    .queue__itemWrapper .m-playing,
    .queue__itemWrapper .m-active,
    .sc-input {
        background-color: #292e42!important;
    }
    .sc-button-like {
        background: transparent!important;
    }
}

